import React from "react";
import FormattedDate from "./FormatDate";
import WeatherIcon from "./WeatherIcon";
import Unit from "./Unit";
import "./WeatherInfo.css";

export default function WeatherInfo(props) {
  return (
    <div className="WeatherInfo">
      <div className="row">
        <div className="col-lg-4 col-md-4 col-sm-4">
          <h1>{props.data.city}</h1>
          <FormattedDate date={props.data.date} />, <br />
          {props.data.description}
        </div>
        <div className="col-lg-4 col-md-4 col-sm-4 d-flex ">
          <WeatherIcon code={props.data.icon} size={30} />
          <div className="temperature-container d-flex justify-content-end">
            <Unit celsius={props.data.temperature} />
          </div>
        </div>
        <div className="col-lg-4 col-md-4 col-sm-4">
          Humidity: <strong>{props.data.humidity}%</strong>,<br />
          Wind: <strong>{props.data.wind}km/h</strong>,<br />
          Pressure: <strong>{props.data.pressure}mmHg</strong>
        </div>
        <br />
      </div>
    </div>
  );
}
