import React from "react";
import FormattedDate from "./FormatDate";
import WeatherIcon from "./WeatherIcon";
import Unit from "./Unit";
import "./Weather.css";

export default function WeatherInfo(props) {
  return (
    <div className="WeatherInfo row mt-3">
      <div className="col-6">
        <h1>{props.data.city} </h1>
        <div className="date-time">
          {" "}
          <FormattedDate date={props.data.date} />, <br />
          {props.data.description}
        </div>
        <div class="col-6 
        ">
          <WeatherIcon code={props.data.icon} size={50} />
        </div>
        <div class="col-6 col-lg-4 temperature">
          <Unit celsius={props.data.temperature} />
        </div>
      </div>
      <div className="col-12 col-lg-5 more-info-weather">
        <ul className="MoreData">
          <li>
            Humidity: <strong>{props.data.humidity}%</strong>
          </li>
          <li>
            Wind: <strong>{props.data.wind}km/h</strong>
          </li>
          <li>
            Pressure: <strong>{props.data.pressure}mmHg</strong>
          </li>
        </ul>
      </div>
    </div>
  );
}
